mapper.select:
SELECT COUNT(*)
FROM Users
WHERE user_account = #{account}

SELECT COUNT(*)
FROM Users
WHERE user_email = #{email}

mapper.update:
UPDATE Users
SET session_id = #{sessionId}, limit_time = #{limitTime}
WHERE user_account = #{account}

UPDATE Users
SET room_id = #{roomId}
WHERE user_account = #{account}

UPDATE Users
SET user_password = #{NewPw}
WHERE user_account = #{account}
